data.solution:
width: int = int(input())
height: int = int(input())
rotated: int = int(input())
rows = []
for i in range(height):
    rows.append(input())

tile_lookup: dict[str,str] = {'#':'Block','+':'Fuel','@':'Gyroscope','$':'Core','^':'Thruster'}

def sanitize(string: str, allowed_chars: str) -> str:
    return ''.join(char for char in string if char in allowed_chars)

def runlength_encode(string: str): # -> generator[str, int]
    current_char: str = string[0]
    repetition_count: int = 0
    for char in string:
        if char == current_char:
            repetition_count += 1
        else:
            yield current_char, repetition_count
            repetition_count = 1
            current_char = char
    yield current_char, repetition_count

# reverses each row and flips, rotating
if rotated == 1 and width > 1 and height > 1:
    rows = [rows[n-1][::-1] for n in range(len(rows),0,-1)]

# removes all characters except # + @ $ ^, and joins the rows into one string
rows = ''.join([sanitize(row, '#+@$^') for row in rows])

# run-length encodes and formats the string, and replaces ascii with tile names
if rows:
    rows = ', '.join([f"{ele[1]} {tile_lookup[ele[0]]}{'s' if ele[1] > 1 else ''}" for ele in runlength_encode(rows)])
else:
    rows = 'Nothing'

print(rows)
