using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution{
    static void Main(string[] args){
        string[] inputs;
        inputs = Console.ReadLine().Split(' ');
        int xa = int.Parse(inputs[0]);
        int xb = int.Parse(inputs[1]);
        int ya = int.Parse(inputs[2]);
        int yb = int.Parse(inputs[3]);
        Console.Error.WriteLine("Line A("+xa+","+ya+") and B("+xb+","+yb+")");
        double a,b;
        a = (xb != xa) ? ((double)(yb-ya)/(xb-xa)) : 0;
        b = ya-a*xa;    
        Console.Error.WriteLine((a==0) ? ("Vertical line x="+xa) : "y="+a+"x + "+b);
        for (int i = 0; i < 5; i++){
            inputs = Console.ReadLine().Split(' ');
            int x = int.Parse(inputs[0]);
            int y = int.Parse(inputs[1]);
            if(y == a*x + b || (a==0 && x==xa)){                        
                Console.WriteLine("The point ("+x+","+y+") is on the line (A,B).");
            }
        }       
    }
}
