import java.util.*;

public class Solution {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int numberOfLines = scanner.nextInt();
        scanner.nextLine();

        StringBuilder sentences = new StringBuilder();
        for (int i = 0; i < numberOfLines; i++) {
            sentences.append(scanner.nextLine()).append(" ");
        }

        String sentenceString = sentences.toString().trim();

        Map<String, Integer> wordCountMap = new HashMap<>();
        String[] words = sentenceString.split("\\s+");

        for (String word : words) {
            wordCountMap.put(word, wordCountMap.getOrDefault(word, 0) + 1);
        }

        List<Map.Entry<String, Integer>> wordCountList = new ArrayList<>(wordCountMap.entrySet());
        wordCountList.sort(Map.Entry.comparingByKey());

        StringBuilder output = new StringBuilder();
        for (Map.Entry<String, Integer> entry : wordCountList) {
            output.append(entry.getKey()).append(" - ").append(entry.getValue()).append(", ");
        }

        if (output.length() > 0) {
            output.setLength(output.length() - 2);
        }

        System.out.println(output.toString());

        Set<Integer> uniqueCounts = new HashSet<>(wordCountMap.values());

        if (uniqueCounts.size() == 1) {
            System.out.println("The Fruit Counts are equal.");
        } else {
            int maxCount = Collections.max(wordCountMap.values());
            List<String> maxCountFruits = new ArrayList<>();
            for (Map.Entry<String, Integer> entry : wordCountMap.entrySet()) {
                if (entry.getValue() == maxCount) {
                    maxCountFruits.add(entry.getKey());
                }
            }
            if (maxCountFruits.size() == 1) {
                System.out.println("The Fruit " + maxCountFruits.get(0) + " has the highest count.");
            } else {
                System.out.println("Fruits are not equal.");
            }
        }

        scanner.close();
    }
}
