def consolidate(sets):
    for idx, this_set in enumerate(sets):
        for next_set in sets[idx + 1:]:
            if this_set.intersection(next_set):
                next_set.update(this_set)
                this_set.clear()
    return [s for s in sets if s]

n_threads = int(input())
threads = [set(input().split(" ")) for thread in range(n_threads)]
print(len(consolidate(threads)))
