import sys
import heapq

def dijkstra(n, graph, origin, destination):
    dist = {i: float('inf') for i in range(n)}
    dist[origin] = 0
    pq = [(0, origin)]  # (cost, node)

    while pq:
        d, node = heapq.heappop(pq)
        if node == destination:
            return d
        if d > dist[node]:
            continue
        for neighbor, weight in graph[node]:
            new_d = d + weight
            if new_d < dist[neighbor]:
                dist[neighbor] = new_d
                heapq.heappush(pq, (new_d, neighbor))

    return dist[destination]

n, m = map(int, input().split())
graph = {i: [] for i in range(n)}

for _ in range(m):
    a, b, t = map(int, input().split())
    graph[a].append((b, t))

origin, destination = map(int, input().split())
print(dijkstra(n, graph, origin, destination))
