#include <iostream>
#include <vector>
#include <algorithm>
#include <iomanip>

using namespace std;

int main() {
    int n;
    cin >> n;
    vector<pair<string, double>> v(n);

    for (auto &[name, avg] : v) {
        cin >> name;
        double sum = 0, x;
        for (int i = 0; i < 4; i++) {
            cin >> x;
            sum += x;
        }
        avg = sum / 4;
    }

    sort(v.begin(), v.end(), [](const auto &a, const auto &b) {
        return a.second > b.second;
    });

    for (int i = 0; i < min(3, n); i++) {
        cout << i + 1 << ": " << v[i].first << " => score: " << fixed << setprecision(2) << v[i].second << '\n';
    }
}
