data.solution:
n = int(input())
names = input().split()
balance = {name: 0 for name in names}

n_expenses = int(input())
for _ in range(n_expenses):
    name, amount = input().split()
    amount = int(amount)
    balance[name] += amount
assert(sum(balance.values()) % n == 0)
share = sum(balance.values()) // n
balance = {k: v-share for k,v in balance.items()}

for name, value in balance.items():
    print(f'{name} {value:+}'.replace('+0','0'))
