const s = readline();

const fruits = ['Apple', 'Banana', 'Cherry', 'Orange', 'Lemon', 'Grape', 'Mango', 'Kiwi', 'Melon'];
const veggies = ['Carrot', 'Pumpkin', 'Celery', 'Cucumber', 'Eggplant', 'Lettuce', 'Onion', 'Peas'];

const words = s.split(' ');
const fruitCount = words.filter(word => fruits.includes(word)).length;
const veggieCount = words.filter(word => veggies.includes(word)).length;

console.log(`Fruits : ${fruitCount}`);
console.log(`Veggies : ${veggieCount}`);
