#include <iostream>
using namespace std;

int main()
{
    int n;
    cin >> n; cin.ignore();
    int level = 0;
    long totalBalls = 0;

    while (totalBalls < n)
    {
        level++;
        totalBalls += level * level;
    }

    cout << (totalBalls == n ? to_string(level) : "cannonball in the sea!") << endl;
}
