data.solution:
program LittleCrossword;

uses
  sysutils;

var
  s,s1 : String;
  i, j, l : Integer;
begin
  readln(s);
  l:=Length(s);
  for i := 1 to l-1 do
  begin
    s1:='';
    for j := 1 to l do
      if s[j] = s[l] then
        s1:=s1+s[i]
      else
        s1:=s1+' ';
    WriteLn(TrimRight(s1));
  end;
  WriteLn(s);
  for i := 2 to l do
  begin
    s1:='';
    for j := 1 to l do
      if s[j] = s[1] then
        s1:=s1+s[i]
      else
        s1:=s1+' ';
    WriteLn(TrimRight(s1));
  end;

  flush(StdErr); flush(output); 
end.
