#include <stdio.h>

int main() {
    int targetX, targetY;  // Position of the target where the shooter wants to place the ball
    int goalkeeper1X, goalkeeper1Y;  // Position of the first goalkeeper's defended zone
    int goalkeeper2X, goalkeeper2Y;  // Position of the second goalkeeper's defended zone

    // Reading the inputs
    scanf("%d %d", &targetX, &targetY);  // Position where the shooter wants to place the ball
    scanf("%d %d", &goalkeeper1X, &goalkeeper1Y);  // Position of the first defended zone of the goalkeeper
    scanf("%d %d", &goalkeeper2X, &goalkeeper2Y);  // Position of the second defended zone of the goalkeeper

    // Check if the shot is out of bounds (width >= 7 or height >= 3)
    if (targetX < 0 || targetX >= 7 || targetY < 0 || targetY >= 3) {
        printf("OFF TARGET\n");  // The shot is outside the goal's limits
    }
    // Check if the goalkeeper intercepts the shot
    else if ((targetX == goalkeeper1X && targetY == goalkeeper1Y) || 
             (targetX == goalkeeper2X && targetY == goalkeeper2Y)) {
        printf("INTERCEPTION\n");  // The goalkeeper intercepted the shot
    }
    // If neither of the previous conditions is met, it's a goal
    else {
        printf("GOAL\n");  // The shot is a goal
    }

    return 0;
}
