#include <iostream>
#include <string>
#include <map>

using namespace std;

int main()
{
    const map<char, bool> B { {'_', false}, {'-', true} };
    map<string, string> inputs;
    size_t inputCount, outputCount;
    string name, signal, type, input1, input2;

    cin >> inputCount >> outputCount; cin.ignore(); //cerr << inputCount << "\n" << outputCount << "\n";
    for (size_t i = 0; i < inputCount; ++i)
    {
        cin >> name >> signal; //cerr << name << " " << signal << "\n";
        inputs[name] = signal;
    }

    const size_t SIZE = signal.size(); 
    for (size_t i = 0; i < outputCount; ++i)
    {
        cin >> name >> type >> input1 >> input2; //cerr << name << " " << type << " " << input1 << " " << input2 << "\n";
        const string& in1 = inputs[input1];
        const string& in2 = inputs[input2];
        cout << name << " ";
        if ("AND" == type) for (size_t i = 0; i < SIZE; ++i) cout << (B.at(in1[i]) && B.at(in2[i]) ? '-' : '_');
        else if ("OR" == type) for (size_t i = 0; i < SIZE; ++i) cout << (B.at(in1[i]) || B.at(in2[i]) ? '-' : '_');
        else if ("XOR" == type) for (size_t i = 0; i < SIZE; ++i) cout << (B.at(in1[i]) ^ B.at(in2[i]) ? '-' : '_');
        else if ("NAND" == type) for (size_t i = 0; i < SIZE; ++i) cout << (!(B.at(in1[i]) && B.at(in2[i])) ? '-' : '_');
        else if ("NOR" == type) for (size_t i = 0; i < SIZE; ++i) cout << (!(B.at(in1[i]) || B.at(in2[i])) ? '-' : '_');
        else if ("NXOR" == type) for (size_t i = 0; i < SIZE; ++i) cout << (!(B.at(in1[i]) ^ B.at(in2[i])) ? '-' : '_');
        else cerr << "ERROR : unknown type -> " << type << "\n";
        cout << "\n";
    }

    return 0;
}
