const laws = readline().split(" ");
const severeLaws = readline().split(" ");
const N = parseInt(readline());
for (let i = 0; i < N; i++)
{
    const crimes = readline().split(" ");
    var sentence = 0;
    for (j in crimes)
    {
        if (laws.includes(crimes[j]))
        {
            sentence++;
        }
        else if (severeLaws.includes(crimes[j]))
        {
            sentence += 6;
        }
    }
    console.log(sentence);
}

// this code firsts gets a list of laws and severe laws, 
// then loops N times to print out how long convict i has to be in prison
// by looping over every "crime" they got accused with and checking it with 
// the laws, then printing a sentence for that.
