#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

//I could have made the solution shorter, but I preferred it to be clear to readers

#define X 20

using namespace std;

typedef long int vett[X];

long int power(int b, int e);

long int combination(int c, int n);

long int maximum(vett v, int nV);

long int minimum(vett v, int nV);

bool workDone(vett v, int nV);

long int power(int b, int e){
     long int p = b;
     if(e==0){
              return 1;
     } else{
            for(int i=1; i<e; i++){
                    p*=b;
            }
            return p;
     }
}
long int combination(int c, int n){
     long int p = power(10, n);
     p*=power(5, (c-n));
     return p;
}
long int maximum(vett v, int nV){
     long int m=0;
     for(int i=0; i<nV; i++){
             if(v[i]>m)
                       m=v[i];
     }
     return m;
}
long int minimum(vett v, int nV){
     long int m=10000000;
     for(int j=0; j<nV; j++){
             if(v[j]<m){
                        if(v[j]>0)
                                  m=v[j];
             }
     }
     return m;
}
bool workDone(vett v, int nV){
     for(int i=0; i<nV; i++){
             if(v[i]!=0)
                        return false;
     }
     return true;
}

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
int main()
{
    int R;
    cin >> R; cin.ignore();
    int V;
    cin >> V; cin.ignore();
    vett vC, vN;
    long int cycles=0;
    for (int i = 0; i < V; i++) {
        int C;
        int N;
        cin >> C >> N; cin.ignore();
        vC[i] = C;
        vN[i] = N;
    }
for(int i=0; i<V; i++){
            vC[i] = combination(vC[i], vN[i]);
    }
    if(R==V){
             cout << maximum(vC, V) << endl;
    }else{
          do{
         //         cerr << "Round" << endl;
                  long int round = minimum(vC, R);
                  cycles+=round;
          //        cerr << cycles << endl;
                  int h=R;
     //             cerr << h << endl;
                  for(int i=0; i<h; i++){
                       //   cerr << C[i] << endl;
                          if(vC[i]>0){                      
           //                          cerr << C[i] << " - " << round <<" = ";
                                     vC[i] -= round;
             //                        cerr << C[i] << endl;
                                     if(vC[i]==0){
                                                if(R<V)
                                                           R++;
                                     }
                          }
                  }
          }while(!workDone(vC, V));
          cout << cycles << endl;
    }
    return 0;
}
