import math
n = int(input())
boba = []
for i in range(n):
    layer = list(map(int,input().split()))
    boba.append(layer)
low = boba[0][0]
high = boba[0][0]
for item in boba:
    for thing in item:
        if thing > high:
            high = thing
        elif thing < low:
            low = thing
new_high = ""
for i in range(len(str(high))-1,-1,-1):
    new_high += str(high)[i]
average = math.ceil((low+int(new_high)+high)/3)
for item in boba:
    counter = 0
    for thing in item:
        if thing > average:
            print("CHOKE")
            counter += 1
            break
    if counter == 0:
        print("SIP")
