#include <map>
#include <iostream>
#include <vector>
using namespace std;

int main(void)
{
    map<char, string> pent = {
        {'A', "F:1"}, {'B', "I:1"}, {'C', "L:1"}, {'D', "N:1"},
        {'E', "P:1"}, {'F', "T:1"}, {'G', "U:1"}, {'H', "V:1"},
        {'I', "W:1"}, {'J', "X:1"}, {'K', "Y:1"}, {'L', "Z:1"},
        {'M', "F:2"}, {'N', "I:2"}, {'O', "L:2"}, {'P', "N:2"},
        {'Q', "P:2"}, {'R', "T:2"}, {'S', "U:2"}, {'T', "V:2"},
        {'U', "W:2"}, {'V', "X:2"}, {'W', "Y:2"}, {'X', "Z:2"},
        {'Y', "F:3"}, {'Z', "I:3"}, {' ', "-"}
    };

    string s;
    vector<string> v;
    getline(cin, s);

    for (int i = 0; i < s.length(); i++)
    {
        v.push_back(pent[s[i]]);
    }

    for (int i = 0; i < v.size(); i++)
    {
        cout << v[i];
        if (i != v.size() - 1)
        {
            cout << ' ';
        }
    }
    cout << endl;

    return 0;
}
