import sys

def count_vertical_strokes(painting):
    strokes = 0
    current_strokes = set()
    for line in painting:
        for index, (c1,c2) in enumerate(zip(line[:-1], line[1:])):
            if c1 != c2:
                if index not in current_strokes:
                    current_strokes.add(index)
                    strokes += 1
            else:
                if index in current_strokes:
                    current_strokes.remove(index)
    return strokes

width, height = map(int, input().split())
painting = [[c for c in input()] for _ in range(height)]

def transpose(arr):
    return [[row[i] for row in arr] for i in range(len(arr[0]))]

print(count_vertical_strokes(painting) + count_vertical_strokes(transpose(painting)))
