using System;

class Solution
{
    static int Count(string line, string key)
    {
        int count = 0;
        for (int k=0; k<line.Length-key.Length+1; ++k)
        {
            count += line.Substring(k,key.Length) == key ? 1:0;
        }
        return count;
    }

    static void Main(string[] args)
    {
        string F = Console.ReadLine();
        int W = int.Parse(Console.ReadLine());
        int H = int.Parse(Console.ReadLine());

        int count = 0;
        string[] block = new string[H];
        for (int i = 0; i < H; i++)
        {
            block[i] = Console.ReadLine();
            count += Count(block[i],F);
        }

        for (int i=0; i<W; ++i)
        {
            string row = "";
            for (int k=0; k<H; ++k)
            {
                row += block[k][i];
            }
            count += Count(row,F);
        }

        Console.WriteLine(count);
    }
}
