import sys
import math
import os 

store = {}
keys  = []
n = int(input())
for i in range(n):
    s = input()
    list = s.split()
    out = ""
    command  = list[0]
    if command == "KEYS":
        res = " ".join(keys)
        if len(res) == 0:
            res = "EMPTY"
        print(res)
        continue
    for item in list[1:]:

        if command == "SET":
           k,v = item.split("=")
           if k not in keys: keys.append(k)
           store[k] = v
        if command == "GET":
           v = store.get(item)
           out += " " + (v if v != None else "null")
        if command == "EXISTS":
             value = store.get(item)
             out += " " + str(value != None).lower()
    if len(out) > 0: 
        print(out.strip())
