#include <stdio.h>
#include <stdlib.h>
#include <string.h>

typedef struct {
    char name[21];
    int points;
    int goals_for;
    int goals_against;
    int goal_diff;
} Team;

int find_team(Team teams[], int count, char* name) {
    for (int i = 0; i < count; i++) {
        if (strcmp(teams[i].name, name) == 0) {
            return i;
        }
    }
    return -1;
}

int compare_teams(const void* a, const void* b) {
    Team* team_a = (Team*)a;
    Team* team_b = (Team*)b;

    // Sort by points (descending)
    if (team_a->points != team_b->points) {
        return team_b->points - team_a->points;
    }

    // Sort by goal difference (descending)
    if (team_a->goal_diff != team_b->goal_diff) {
        return team_b->goal_diff - team_a->goal_diff;
    }

    // Sort by name (ascending)
    return strcmp(team_a->name, team_b->name);
}

int main() {
    int n;
    scanf("%d", &n);

    Team teams[40]; // Max 20 matches = max 40 teams
    int team_count = 0;

    for (int i = 0; i < n; i++) {
        char team_a[21], team_b[21];
        int score_a, score_b;
        scanf("%s %s %d %d", team_a, team_b, &score_a, &score_b);

        // Find or create team A
        int idx_a = find_team(teams, team_count, team_a);
        if (idx_a == -1) {
            idx_a = team_count++;
            strcpy(teams[idx_a].name, team_a);
            teams[idx_a].points = 0;
            teams[idx_a].goals_for = 0;
            teams[idx_a].goals_against = 0;
        }

        // Find or create team B
        int idx_b = find_team(teams, team_count, team_b);
        if (idx_b == -1) {
            idx_b = team_count++;
            strcpy(teams[idx_b].name, team_b);
            teams[idx_b].points = 0;
            teams[idx_b].goals_for = 0;
            teams[idx_b].goals_against = 0;
        }

        // Update goals
        teams[idx_a].goals_for += score_a;
        teams[idx_a].goals_against += score_b;
        teams[idx_b].goals_for += score_b;
        teams[idx_b].goals_against += score_a;

        // Update points
        if (score_a > score_b) {
            teams[idx_a].points += 3;
        } else if (score_a < score_b) {
            teams[idx_b].points += 3;
        } else {
            teams[idx_a].points += 1;
            teams[idx_b].points += 1;
        }
    }

    // Calculate goal differences
    for (int i = 0; i < team_count; i++) {
        teams[i].goal_diff = teams[i].goals_for - teams[i].goals_against;
    }

    // Sort teams
    qsort(teams, team_count, sizeof(Team), compare_teams);

    // Output results
    for (int i = 0; i < team_count; i++) {
        printf("%s %d %d\n", teams[i].name, teams[i].points, teams[i].goal_diff);
    }

    return 0;
}
