import re

# Parse input
formula = input()
prefixes = ['meth', 'eth', 'prop', 'but', 'pent', 'hex', 'hept', 'oct', 'non', 'dec']

# Initialize atom counts
C = H = O = 0

# Count atoms using regex
matches = re.findall(r'(C|H|O)(\d*)', formula)
for atom, count in matches:
    val = int(count) if count else 1
    if atom == 'C':
        C += val
    elif atom == 'H':
        H += val
    elif atom == 'O':
        O += val

# Check for functional group at end

suffix = ''
if H - 1 == 2 * C - 1 and formula.endswith('COOH') and O == 2:
    suffix = 'anoic acid'
elif H - 1 == 2 * C + 1 and formula.endswith('OH') and O == 1:
    suffix = 'anol'
elif H == 2 * C + 2 and O == 0:
    suffix = 'ane'
elif H == 2 * C and formula.endswith('CHO') and O == 1:
    suffix = 'anal'
elif H== 2 * C and formula.find('CO') != -1 and O == 1: 
    suffix = 'anone'
elif H == 2 * C and O == 0: 
    suffix = 'ene'
else: 
    suffix = 'OTHERS'

# Determine name
name = prefixes[C - 1] + suffix if suffix != 'OTHERS' else suffix
print(name)
