from collections import Counter
n = int(input())
atoms = [input().strip() for _ in range(n)]
counts = Counter(atoms)
formula = ""
if 'C' in counts:
    formula += 'C' + (str(counts['C']) if counts['C'] > 1 else '')
if 'H' in counts:
    formula += 'H' + (str(counts['H']) if counts['H'] > 1 else '')
for element in sorted(counts):
    if element not in ('C', 'H'):
        formula += element + (str(counts[element]) if counts[element] > 1 else '')
print(formula)
