H, W = map(int, input().split())  # Height and width of the land grid
a, b = map(int, input().split())  # Height and width of the house
grid = [list(map(int, input().strip())) for _ in range(H)]

# Try every top-left starting point where the house would fit
for row in range(H - a + 1):
    for col in range(W - b + 1):
        base_height = grid[row][col]  # Height of the top-left cell
        valid = True

        # Check all cells in the house area
        for i in range(a):
            for j in range(b):
                if grid[row + i][col + j] != base_height:
                    valid = False
                    break
            if not valid:
                break

        if valid:
            print(f"{row} {col}")
            exit()

# If no suitable spot is found
print("Not Found")
