def val(s):
    try:
        return float(s)
    except:
        return sum(ord(c) for c in s)

n = int(input())
arr = [val(input()) for _ in range(n)]

if len(set(arr)) < n:
    print(-1)
    exit()

indexed = list(enumerate(arr))
indexed.sort(key=lambda x: x[1])

visited = [False]*n
swaps = 0

for i in range(n):
    if visited[i] or indexed[i][0] == i:
        continue
    cycle_size = 0
    j = i
    while not visited[j]:
        visited[j] = True
        j = indexed[j][0]
        cycle_size += 1
    if cycle_size > 1:
        swaps += cycle_size - 1

print(swaps)

