class Obstacle:
    def __init__(self, co1, co2):
        self.co1 = co1
        self.co2 = co2

    def intersect(self, co):
        for comp, compmin, compmax in zip(co, self.co1, self.co2):
            if not compmin <= comp <= compmax:
                return False
        return True

n = int(input())
maze_size = [int(i) for i in input().split(",")]
starting_position = tuple(int(i) for i in input().split(","))
destination = tuple(int(i) for i in input().split(","))
b = int(input())
obstacles = set()
for i in range(b):
    co1, co2 = input().split()
    co1 = tuple(int(i) for i in co1.split(","))
    co2 = tuple(int(i) for i in co2.split(","))
    obstacles.add(Obstacle(co1, co2))

def get_neighbors(pos):
    neighbors = set()
    for d in range(n):
        if pos[d] > 0:
            ne = (*pos[:d], pos[d]-1, *pos[d+1:])
            neighbors.add(ne)
        if pos[d] < maze_size[d]-1:
            ne = (*pos[:d], pos[d]+1, *pos[d+1:])
            neighbors.add(ne)
    return neighbors

def path(starting_position, destination):
    queue = [(starting_position, 0)]
    visited = {starting_position}
    while queue:
        pos, l = queue.pop(0)
        if pos == destination:
            return l
        for neigh in get_neighbors(pos):
            if not any(ob.intersect(neigh) for ob in obstacles) and neigh not in visited:
                queue.append((neigh, l+1))
                visited.add(neigh)
    else:
        return -1

l = path(starting_position, destination)
if l == -1:
    print("NO PATH")
else:
    print(l)
