import math

def char_to_int(c):
    if c.isdigit():
        return int(c)
    return ord(c.upper()) - ord('A') + 10

def int_to_char(val):
    if val <= 9:
        return str(val)
    return chr(ord('A') + val - 10)

w = int(input())
l = int(input())
grid = [input().strip() for _ in range(w)]

brightness = [[0 for _ in range(l)] for _ in range(w)]

def is_lit(sx, sy, x, y, r):
    dx = x - sx
    dy = y - sy
    dist = math.sqrt(dx*dx + dy*dy)
    d = round(dist)
    if d <= r:
        if dist <= r - 0.5:
            return r - d
    return 0

for sy in range(w):
    for sx in range(l):
        ch = grid[sy][sx]
        if ch != '.':
            r = char_to_int(ch)
            if r == 0:
                continue
            for y in range(max(0, sy - r - 1), min(w, sy + r + 2)):
                for x in range(max(0, sx - r - 1), min(l, sx + r + 2)):
                    b = is_lit(sx, sy, x, y, r)
                    brightness[y][x] += b

for row in brightness:
    print(''.join(int_to_char(min(35, val)) for val in row))
