class Solution {

    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int alphabet = 65;
        for (int i = 0; i < n; i++){
            for (int j = 0; j <= i; j++){
                System.out.print((char) (alphabet + j));
            }
            System.out.println();
        }
    }
}
