using namespace std;

  /**
 * Calcule la somme des diviseurs propres d'un nombre
 * (tous les diviseurs sauf le nombre lui-même)
 */
int sumOfProperDivisors(int num) {
    int sum = 1; // 1 est toujours un diviseur propre

    // Chercher les diviseurs de 2 à sqrt(num)
    for (int i = 2; i * i <= num; i++) {
        if (num % i == 0) {
            sum += i; // Ajouter le diviseur i

            // Si i != num/i, ajouter aussi l'autre diviseur
            if (i != num / i) {
                sum += num / i;
            }
        }
    }

    return sum;
}

int main() {
    int m, n;
    cin >> m; cin.ignore();
    cin >> n; cin.ignore();

    // Vérifier les contraintes
    if (m <= 100 || m >= 100000 || n <= 100 || n >= 100000) {
        cout << "Invalid" << endl;
        return 0;
    }

    // Vérifier que m != n
    if (m == n) {
        cout << "Invalid" << endl;
        return 0;
    }

    // Calculer la somme des diviseurs propres de chaque nombre
    int sumM = sumOfProperDivisors(m);
    int sumN = sumOfProperDivisors(n);

    // Vérifier si les nombres sont amicaux
    // La somme des diviseurs de m doit égaler n
    // ET la somme des diviseurs de n doit égaler m
    if (sumM == n && sumN == m) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}

