import java.util.*;
import java.math.*;

//Update 05.06.2018
class Fraction {
	BigInteger counters;
	BigInteger names;
	public Fraction(BigInteger fatorial, BigInteger fatorial2) {
		this.counters = fatorial;
		this.names = fatorial2;
	}

	/**
	 * Reduces the fraction, divides counter and name by their greatest common divisor
	 * @return
	 */
	public Fraction reduceFraction() {
		BigInteger ggt = counters.gcd(names);
		counters = counters.divide(ggt);
		names = names.divide(ggt);
		return this;
	}

	/**
	 * Multiplies this Fraction with Fraction b2
	 * @param b2
	 * @return
	 */
	public Fraction multiply(Fraction b2) {
		Fraction b = new Fraction(counters.multiply(b2.counters), names.multiply(b2.names));
		return b.reduceFraction();
	}

	/**
	 * Divides this Fraction with Fraction b2
	 * @param b2
	 * @return
	 */
	public Fraction divide(Fraction b2) {
		Fraction b = new Fraction(b2.names, b2.counters);
		return this.multiply(b);
	}

	public String toString() {
		return counters.toString() + "/" + names.toString();
	}
}

public class Solution {
	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();//Number of balls
		int W = in.nextInt();//Number of white balls in bag
		int n = in.nextInt();//Size of sample
		int k = in.nextInt();//Desired number of white balls

		Fraction b1 = binom(W, k);
		Fraction b2 = binom(N - W, n - k);
		Fraction b3 = binom(N, n);
		Fraction b4 = b1.multiply(b2).divide(b3);
		System.out.println(b4.counters + ":" + b4.names.subtract(b4.counters));
		in.close();
	}

	/**
	 * Calculates the binomial coefficient of n [over] k
	 * @param n
	 * @param k
	 * @return
	 */
	public static Fraction binom(int n, int k) {
		long total = 1;
		for (int i = 1; i <= k; i++) {
			total *= (n - i + 1);
			total /= i;
		}
		return new Fraction(new BigInteger("" + total), BigInteger.ONE);
	}
}
