#include <iostream>
#include <string>
#include <iomanip>
#include <sstream>
using namespace std;
string generateSerialKey(const string& username)
{
    int ascii_sum = 0;
    for (char c : username)
    {
        ascii_sum += int(c);
    }
    int length = username.size();
    int seed = (ascii_sum * length) ^ 20480;
    unsigned int first = seed & 0xFFFF;
    unsigned int second = (seed >> 16) & 0xFFFF;
    unsigned int third = (int(username[0]) + int(username.back())) * length;
    unsigned int final = (first + second + third) % 0x10000;
    stringstream ss;
    ss << uppercase << hex
       << setw(4) << setfill('0') << first << '-'
       << setw(4) << setfill('0') << second << '-'
       << setw(4) << setfill('0') << third << '-'
       << setw(4) << setfill('0') << final;
    return ss.str();
}
int main()
{
    string username;
    getline(cin, username);
    cout << generateSerialKey(username) << endl;
    return 0;
}
