items = []

linecount = int(input())
for i in range(linecount):
    command = input().split(" ")
    if command[0] == 'ADD' :
        items.append(command[1:])
    elif command[0] == 'REMOVE' :
        items = [x for x in items if x[0] != command[1]]
    elif command[0] == 'INVENTORY':
        print("INVENTORY", ", ".join([x[0] for x in items]),"TOTALS",sum([int(x[1]) for x in items]),"GOLD")
