w, h = map(int, input().split())
grid = [input() for _ in range(h)]

i = j = 0
for _ in range(int(input())):
    steps, _, direction = input().split()
    steps = int(steps)
    if direction == 'North':
        i -= steps
    elif direction == 'South':
        i += steps
    elif direction == 'East':
        j += steps
    elif direction == 'West':
        j -= steps

if grid[i][j] == 'X':
    print('Chase after them!')
elif grid[i][j] == 'x':
    print('Find the real treasure!')
else:
    print('They\'ll be back.')
