using namespace std;
#include <bits/stdc++.h>
const string alphabet = "abcdefghijklmnopqrstuvwxyz";
int main() {
    string word;
    getline(cin,word);
    string letters;
    getline(cin,letters);
    string first = word.substr(0,word.find('_'));
    string second = word.substr(word.find('_')+1,word.length()-1);
    vector<char> alpha_list;
    for (int i=0;i<alphabet.length();i++) {
        alpha_list.push_back(alphabet[i]);
    }
    set<char> used;
    for (int i=0;i<letters.length();i++) {
        if (letters[i]!='_' && !used.count(letters[i])) {
            alpha_list.erase(find(alpha_list.begin(),alpha_list.end(),letters[i]));
            used.insert(letters[i]);
        }
    }
    for (int i=0;i<word.length();i++) {
        if (word[i]!='_' && !used.count(word[i])) {
            alpha_list.erase(find(alpha_list.begin(),alpha_list.end(),word[i]));
            used.insert(word[i]);
        }
    }
    for (int i=0;i<alpha_list.size();i++) {
        cout << first << alpha_list[i] << second << endl;
    }
    return 0;
}
