var N = parseFloat(readline());
var L = parseInt(readline());

var R=[Math.floor(N)];
for(let i=1;i<L;i++) {
  N-=R[R.length-1];
  N=1/N;
  R.push(Math.floor(N));
}

print('['+R.join(',')+']');
