cookie = int(input()) 
n = int(input()) 
attacks = list(map(int, input().split())) 
current = cookie 
for stolen in attacks: 
    if stolen == 0: 
        print("SAFE") 
    elif stolen > current: 
        print("CHEATER") 
    else: 
        current -= stolen 
        if current == 0: 
            print("EMPTY") 
            break 
        else: 
            print("WARNING")
