var n = readline();

function cycle(n) {
    let a=[n];
    while(true) {
        n = a[a.length-1];
        let An = parseInt(n.split('').sort((a,b)=>b-a).join(''));
        let Dn = parseInt(n.split('').sort((a,b)=>a-b).join(''));
        let Kn=An-Dn;
        for(let i=a.length-1;i>=0;i--) {
            if(parseInt(a[i])===Kn) {
                return a.slice(i);
            }
        }

        let KnStr=""+Kn;
        //padding
        KnStr="0".repeat(a[0].length-KnStr.length)+KnStr;
        a.push(KnStr);
    }
}

print(cycle(n).join(' '));
