offset1, offset2 = input().split()

def to_minutes(s):
    return (1 if s[0] == '+' else -1) * (int(s[1:3]) * 60 + int(s[3:5]))

def time_str(m):
    m %= 1440
    return f"{m//60:02d}{m%60:02d}"

m1, m2 = to_minutes(offset1), to_minutes(offset2)
time_points = [(time_str(i + m1), time_str(i + m2)) for i in range(1440)]
time_anagrams = sorted((t1, t2) for t1, t2 in time_points if sorted(t1) == sorted(t2))

print("\n".join(f'{t1}, {t2}' for t1, t2 in time_anagrams))
