#include <iostream>
#include <vector>
using namespace std;

int main()
{
    int n;
    cin>>n;
    cin.ignore();
    vector<string> v;
    for (int i = 0; i < n; i++){
        string s;
        getline(cin, s);
        v.push_back(s);
    }
    int indice=-1;
    float percentage=0;
    for (int i = 0; i < n; i++){
        int nbLower=0;
        for (char c :v.at(i)){
            if (islower(c)) nbLower++;
        }
        float p = nbLower*100.0/v.at(i).size();
        if (p > percentage){
            percentage = p;
            indice = i;
        }

        cerr << nbLower << " " << p << " " << percentage << endl;
    }
    cout << v.at(indice) << endl;
}
