#Intuitive version
import math

r=input()
s=input().replace('x', '*')
ns=""

if r=="yes":
    for i in range(len(s)):
        if s[i]=="+":
            ns+="-"
        elif s[i]=="-":
            ns+="+"
        elif s[i]=="*":
            ns+="/"
        elif s[i]=="/":
            ns+="*"
        else:
            ns+=s[i]
    s=ns

if "/ 0" in s:
    print("Error")
else:
    num, stack, op = 0, [], '+'
    s += '+'
    for c in s:
        if c.isdigit():
            num = num * 10 + int(c)
        elif c in '+-*/':
            if op == '+':
                stack.append(num)
            elif op == '-':
                stack.append(-num)
            elif op == '*':
                stack[-1] *= num
            elif op == '/':
                stack[-1] = stack[-1] / num
            op = c
            num = 0

    r = sum(stack)
    print(math.ceil(r))

