using System;

class Solution
{
    static void Main()
    {
        long N = long.Parse(Console.ReadLine()!);
        long S = long.Parse(Console.ReadLine()!);
        long Q = long.Parse(Console.ReadLine()!);

        long T = N * (N + 1) / 2;
        long U = N * (N + 1) * (2 * N + 1) / 6;

        long A = T - S;
        long B = U - Q;
        long ab = (A * A - B) / 2;

        long D = A * A - 4 * ab;
        long r = ISqrt(D);

        long a = (A - r) / 2;
        long b = (A + r) / 2;
        if (a > b) { long t = a; a = b; b = t; }

        Console.WriteLine($"{a} {b}");
    }

    static long ISqrt(long x)
    {
        long lo = 0, hi = 3037000500;
        while (lo <= hi)
        {
            long mid = (lo + hi) >> 1;
            long sq = mid * mid;
            if (sq == x) return mid;
            if (sq < x) lo = mid + 1;
            else hi = mid - 1;
        }
        return hi;
    }
}
