def kibble(weight) :
    if weight <= 5 :
        return 80
    elif weight <= 10 :
        return 150
    elif weight <= 20 :
        return 260
    elif weight <= 30 :
        return 375
    elif weight <= 40 :
        return 470
    elif weight <= 50 :
        return 560
    else :
        return 600

s = 0
