def pgcd(a,b):
  a,b=max(a,b),min(a,b)
  while a:
    b,a=a,b%a
  return b

def bezout(n,a):
  r,u,v,rr,uu,vv=a,1,0,n,0,1
  while rr:
    q=r//rr
    r,u,v,rr,uu,vv=rr,uu,vv,r-q*rr,u-q*uu,v-q*vv
  if u<0:
    u+=n
  return u

n=int(input())
liste=[]
for _ in range(n):
    n,a=(int(i) for i in input().split())
    if pgcd(n,a)==1:
        liste.append(str(bezout(n,a)))
    else:
        liste.append("NaN")
print(" ".join(liste))
