import java.util.*;
import java.util.stream.IntStream;

class Solution {

    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        CompareType type = CompareType.of(in.next());
        List<Line> linesV1 = readLines(in);
        List<Line> linesV2 = readLines(in);

        List<String> diffs = compare(type, linesV1, linesV2);
        if ( diffs.isEmpty() ) {
            System.out.println("No Diffs");
        } else {
            diffs.forEach(System.out::println);
        }
    }


    private static List<Line> readLines(Scanner in) {
        int nbLines = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        return IntStream.range(0, nbLines).mapToObj(i -> new Line(in.nextLine(), i+1)).toList();
    }

    public static List<String> compare(CompareType compareType, List<Line> lines, List<Line> updatedLines) {
        Map<String, Line> oldMap = buildMap(compareType, lines);
        Map<String, Line> newMap = buildMap(compareType, updatedLines);

        List<String> diffs = new LinkedList<>();
        for(Map.Entry<String, Line> entry : newMap.entrySet()) {
            String id = entry.getKey();
            Line current = entry.getValue();
            Line previous = oldMap.get(id);
            if ( previous == null ) {
                diffs.add("ADD: " + current.value());
            } else {
                oldMap.remove(id);
                if ( !previous.value().equals(current.value()) ) {
                    diffs.add("CHANGE: " + previous.value() + " ---> " + current.value());
                } else if ( previous.number() != current.number() ) {
                    diffs.add("MOVE: " + previous.value() + " @:" + previous.number() + " >>> @:" + current.number());
                }
            }
        }

        oldMap.values().stream().map(l -> "DELETE: " + l.value()).forEach(diffs::add);

        diffs.sort(Comparator.naturalOrder());

        return diffs;
    }

    private static Map<String, Line> buildMap(CompareType compareType, List<Line> lines) {
        Map<String, Line> map = new HashMap<>();
        for(int i=0; i<lines.size(); i++){
            String id = getId(compareType, i, lines.get(i).value());
            map.put(id, lines.get(i));
        }
        return map;
    }

    private static String getId(CompareType compareType, int number, String text) {
        return switch(compareType) {
            case BY_NUMBER -> ""+number;
            case BY_CONTENT -> text;
        };
    }

    enum CompareType {
        BY_NUMBER,
        BY_CONTENT;
        public static CompareType of(String value) {
            return "BY_NUMBER".equals(value) ? BY_NUMBER : BY_CONTENT;
        }
    }

}

record Line (String value, int number) {}
