using System;

public class Solution
{
    public static void Main(string[] args)
    {
        int fuel = int.Parse(Console.ReadLine());
        float AverageFuelConsuption = float.Parse(Console.ReadLine());

        int km = int.Parse(Console.ReadLine());

        float KmWithFuel = (fuel * 100) /AverageFuelConsuption;

        if (fuel == 0)
        {
            Console.WriteLine("There is no fuel in the tank.");
        }
        else
        {
            if (km < KmWithFuel)
                Console.WriteLine("Perfect.");
            else if (km == KmWithFuel)
                Console.WriteLine("50/50. On your own risk.");
            else if (km > KmWithFuel && fuel > 0)
                Console.WriteLine("There isn't enough fuel.");
        }
    }
}
