let pAces = 0;
let pScore = 0;

const p = parseInt(readline());
var inputs = readline().split(' ');
for (let i = 0; i < p; i++) {
    const point = inputs[i];
    if (point === "A") {
        pAces++;
        pScore += 11;
    } else pScore += Number(point) || 10;

    if (pScore > 21 && pAces) {
        pAces--;
        pScore -= 10;
    } else if (pScore > 21) pScore = -Infinity;
}

let cAces = 0;
let cScore = 0;

const c = parseInt(readline());
var inputs = readline().split(' ');
for (let i = 0; i < c; i++) {
    const point = inputs[i];
    if (point === "A") {
        cAces++;
        cScore += 11;
    } else cScore += Number(point) || 10;

    if (cScore > 21 && cAces) {
        cAces--;
        cScore -= 10;
    } else if (cScore > 21) cScore = -Infinity;
}

const winner = [];
if (pScore > cScore) winner.push("you", pScore);
else winner.push("dealer", cScore);

console.log(...winner);
