const D = parseInt(readline()); 
const N = parseInt(readline()); 

let bestIndex = -1;  
let maxHP = -1;     

for (let i = 0; i < N; i++) {
    const H = parseInt(readline()); 

    if (H <= D && H > maxHP) {
        maxHP = H;
        bestIndex = i;
    }
}

// Affichage du résultat
if (bestIndex !== -1) {
    console.log(bestIndex);
} else {
    console.log("RETREAT");
}
