#include <stdio.h>
#include <stdlib.h>
#include <string.h>


int main()
{
    int N;
    scanf("%d", &N);

    char buffer[10] = {'\0'};
    sprintf(buffer, "%i", N);

    char output[10] = {'\0'};

    int starting = 0;
    int ending = strlen(buffer) - 1;

    for (int i = 0; i < strlen(buffer); i++) {
        if (buffer[i] < '5') {
            output[starting] = buffer[i];
            starting++;
        } else {
            output[ending] = buffer[i];
            ending -= 1;
        }
    }

    printf("%s\n", output);

    return 0;
}
