import fractions as f

départ,arrivée=input().split(" in ")
n=int(input())
graphe=dict()
for _ in range(n):
    a,b=input().split(" = ")
    n1,u1=a.split(maxsplit=1)
    n2,u2=b.split(maxsplit=1)
    if u1 in graphe:
        graphe[u1][u2]=f.Fraction(n2+"/"+n1)
    else:
        graphe[u1]={u2:f.Fraction(n2+"/"+n1)}
    if u2 in graphe:
        graphe[u2][u1]=f.Fraction(n1+"/"+n2)
    else:
        graphe[u2]={u1:f.Fraction(n1+"/"+n2)}

àfaire={départ}
valeurs={départ:1}

while arrivée not in valeurs:
    temp=set()
    for unité in àfaire:
        for nouvelle in graphe[unité]:
            if nouvelle not in temp|àfaire and nouvelle not in valeurs:
                valeurs[nouvelle]=valeurs[unité]*graphe[unité][nouvelle]
                temp.add(nouvelle)
    àfaire=set(temp)

v=valeurs[arrivée]
d=v.denominator
a=v.numerator
print("%d %s = %d %s"%(d,départ,a,arrivée))
