class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int e = in.nextInt();
        int f = in.nextInt();
        int s = in.nextInt();
        int b = in.nextInt();
        // Write an answer using System.out.println()
        // To debug: System.err.println("Debug messages...");
        //find max cake
        int maxcake = 0;
        int ec =e, fc =f, sc =s, bc =b;
        while (ec >= 3 && fc >= 180 && sc >= 100 && bc >= 100){
            maxcake ++;
            ec = ec - 3; fc = fc - 180; sc = sc - 100; bc = bc - 100;
        }

        //find max cookie
        int maxcoo = 0;
        int eco =e, fco =f, sco =s, bco =b;
        while (eco >= 1 && fco >= 100 && sco >= 150 && bco >= 50){
            maxcoo ++;
            eco = eco - 1; fco = fco - 100; sco = sco - 150; bco = bco - 50;
        }

        //find max muffin
        int maxm = 0;
        int em =e, fm =f, sm =s, bm =b;
        while (em >= 2 && fm >= 150 && sm >= 100 && bm >= 150){
            maxm ++;
            em = em - 2; fm = fm - 150; sm = sm - 100; bm = bm - 150;
        }
        if(maxm > maxcake && maxm > maxcoo){
            System.out.println(maxm+" Muffin");
        }
        if(maxcake >= maxcoo && maxcake >= maxm){
            System.out.println(maxcake+" Cake");
        }
        if(maxcoo > maxcake && maxcoo >= maxm){
            System.out.println(maxcoo+" Cookie");
        }
    }
}
