// Key generator (TypeScript)
const message = "a sample message";
const desiredOutput = "coffee";

const calculateKeys = (hexes: string[], desiredOutput: string) => {
	const charCodes = desiredOutput.split("").map((char) => char.charCodeAt(0));
	return charCodes.map((char, i) => char - parseInt(hexes[i], 16));
};
const getHexes = (message: string) =>
	message.replace(/[^abcdef]/g, "").split("");

const hexes = getHexes(message);
if (hexes.length !== desiredOutput.length) {
	console.log("message:", message);
	console.log("hex digits in message:", hexes.join(""), "(", hexes.length, ")");
	console.log("desired output:", desiredOutput, "(", desiredOutput.length, ")");
	console.log("need", desiredOutput.length - hexes.length, "hex digits in the message");
	process.exit();
}
const keys = calculateKeys(hexes, desiredOutput).join(" ");
console.log(keys);
`
